
const delay=400
window.addEventListener('DOMContentLoaded',function(){const menuItems=document.querySelectorAll('.menu__item')
const header=document.querySelector('.header')
function openMenu(item,submenu){closeAllMenus()
item.classList.add('active')
submenu.classList.add('active')}
function closeMenu(item,submenu){item.classList.remove('active')
submenu.classList.remove('active')}
function closeAllMenus(){menuItems.forEach(item=>{item.classList.remove('active')
const submenu=item.querySelector('.submenu')
if(submenu){submenu.classList.remove('active')}})}
for(let index=0;index<menuItems.length;index++){const item=menuItems[index]
const subitem=item.querySelector('.menu__subitem')
const submenu=item.querySelector('.submenu')
if(!subitem||!submenu)continue
item.addEventListener('mouseenter',function(){openMenu(item,submenu)})
header.addEventListener('mouseleave',function(){closeMenu(item,submenu)})}
function lockBody(){const scrollWidth=window.innerWidth-document.body.clientWidth
document.body.classList.toggle('lock')
document.body.style.paddingRight=scrollWidth+'px'}})